import UIKit

struct MapScreenConfigurator {

	static func createModule() -> UIViewController {
        var view: MapScreenViewController!

        let viewController = UIStoryboard.init(name: "MapScreen", bundle: Bundle.main).instantiateInitialViewController()
        if viewController == nil {
            fatalError("Seems there is no initial view controller in MapScreen.storyboard")
        }

        if viewController is UINavigationController {
            view = (viewController as? UINavigationController)?.viewControllers.first as? MapScreenViewController
        } else {
            view = viewController as? MapScreenViewController
        }

        let interactor = MapScreenInteractor()
        let router = MapScreenRouter()
        let presenter = MapScreenPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return viewController!
    }
}
