import UIKit

struct PhotosConfigurator {

	static func createModule() -> UIViewController {
        var view: PhotosViewController!

        let viewController = UIStoryboard.init(name: "Photos", bundle: Bundle.main).instantiateInitialViewController()
        if viewController == nil {
            fatalError("Seems there is no initial view controller in Photos.storyboard")
        }

        if viewController is UINavigationController {
            view = (viewController as? UINavigationController)?.viewControllers.first as? PhotosViewController
        } else {
            view = viewController as? PhotosViewController
        }

        let interactor = PhotosInteractor()
        let router = PhotosRouter()
        let presenter = PhotosPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return viewController!
    }
}
